mapper.select:
SELECT id, act_id
FROM activity_join
where client_user_id = #{clientUserId}

SELECT COUNT(*)
FROM activity_join
where client_user_id = #{clientUserId}
  and act_id = #{actId}

mapper.insert:
insert into activity_join
set act_id         = #{actId},
    client_user_id = #{clientUserId}
